import platform
import time

import numba
import numpy as np

stri = str(" ")
# ^ This is the string I used to compare my versions of the code. The first sequence of 14 distinct chars is at index 179
stri2 = str(" ")
# ^ This is the string I used to compare my version of the code to the version made by https://github.com/david-a-perez/. The first sequence of 14 distinct chars is at index 3822
# For this string my algorythm needs around 2500ns
# The Strings need to end with a space. It is used for bounds-checking

length_ = 14


@numba.njit(nogil=True, cache=True)
def withBitmask(s, l=length_):  # Return code -2 means the algorithm reached the end of the string
    index = 0

    while True:

        length = oldSeen = 0
        lengthEnd = index + l
        subS = s[index:lengthEnd][::-1]

        for char in subS:

            byteChar = 1 << (char & 0x1f)  # 9f wasn't the right bitmask
            if byteChar & 1:
                return -2  # if the algorythm reaches the space it returns -2

            length += 1

            if length != 1:

                if oldSeen & byteChar:
                    index = lengthEnd - length + 2
                    break

                elif length == l:
                    return index - 1

            oldSeen |= byteChar


assert stri[-1] == " "

s_ = np.array([ord(x) for x in "oeisrycvknwzqa "], dtype="u2")  # precompile and test algorithm.
assert withBitmask(s_) == -1

n = 1000000
out = -1
s_ = time.time()  # store starting time

b = np.array([ord(x) for x in stri], dtype="u2")  # precompute string
for i in range(n):
    out = withBitmask(b)

t = time.time() - s_  # compute time it took for n iterations

print(f"Python version: {platform.python_version()};\ntotal: {t}s;\ntook {round((t / n) * 1e9, 3)}ns ({round(1 / (t / n), 3)}iters/s) to find string \"{stri[out:out + length_]}\" at position {out};")
